import React, { Component } from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";


class Main extends Component {

  render() {
    return (
      <Router>
      <div>
        <nav>
          <ul>
            <li>
              <Link to="/">Home</Link>
            </li>
            <li>
              <Link to="/artists">Artists</Link>
            </li>
            <li>
              <Link to="/users">Users</Link>
            </li>
          </ul>
        </nav>

        {/* A <Switch> looks through its children <Route>s and
            renders the first one that matches the current URL. */}
        <Switch>
          <Route path="/artists">
            <Artists />
          </Route>
          <Route path="/users">
            <Users />
          </Route>
          <Route path="/">
            <Home />
          </Route>
        </Switch>
      </div>
    </Router>,
      <div>
        <p>&nbsp;</p>
        <h2>Buy Portrait</h2>
        <table className="table">
          <thead>
            <tr>
              <th scope="col">#</th>
              <th scope="col">Description</th>
              <th scope="col">Price</th>
              <th scope="col">Owner</th>
              <th scope="col"></th>
            </tr>
          </thead>
          <tbody id="productList">
            { this.props.products.map((product, key) => {
              return(
                <tr key={key}>
                  <th scope="row">{product.id.toString()}</th>
                  <td>{product.name}</td>
                  <td>{window.web3.utils.fromWei(product.price.toString(), 'Ether')} ETH</td>
                  <td>{product.owner}</td>
                  <td>
                    { !product.purchased
                      ? <button
                          name={product.id}
                          value={product.price}
                          onClick={(event) => {
                            this.props.purchaseProduct(event.target.name, event.target.value)
                          }}
                        >
                          Buy
                        </button>
                      : null
                    }
                    </td>
                </tr>
              )
            })}
          </tbody>
        </table>
      </div>
    );
  }
}

export default Main;


function Home() {
  return <h2>Home</h2>;
}

function Artists() {
  return (<h2>Artists</h2>, <h2>Add a new Portrait</h2>,
        <form onSubmit={(event) => {
          event.preventDefault()
          const name = this.productName.value
          const price = window.web3.utils.toWei(this.productPrice.value.toString(), 'Ether')
          this.props.createProduct(name, price)
        }}>
            <div className="form-group mr-sm-2">
                {/* <label for="myfile">Select a file:</label> */}
                <input type="file" id="myfile" name="myfile" placeholder="Portrait Picture" className="form-control" />   
            </div>

            {/* <div className="form-group mr-sm-2">
                <input
                id="productDesc"
                type="text"
                ref={(input) => { this.productDesc = input }}
                className="form-control"
                placeholder="Description"
                required />
            </div> */}

            <div className="form-group mr-sm-2">
                <input
                id="productPrice"
                type="text"
                ref={(input) => { this.productPrice = input }}
                className="form-control"
                placeholder="Price"
                required />
            </div>

          <div className="form-group mr-sm-2">
            <input
              id="productName"
              type="text"
              ref={(input) => { this.productName = input }}
              className="form-control"
              placeholder="Artist"
              required />
          </div>

          <center><button type="submit" className="btn btn-primary"> SAVE </button></center>
        </form>
  );
}

function Users() {
  return <h2>Users</h2>;
}
