import React, { Component } from 'react'; 
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
//import logo from '../logo.png';
import './App.css';
import App from './App'
 

class Artists extends Component {

  render() {
    return (
      <h2>Artists</h2>, <h2>Add a new Portrait</h2>,
        <form onSubmit={(event) => {
          event.preventDefault()
          const name = this.productName.value
          const price = window.web3.utils.toWei(this.productPrice.value.toString(), 'Ether')
          this.props.createProduct(name, price)
        }}>
            <div className="form-group mr-sm-2">
                {/* <label for="myfile">Select a file:</label> */}
                <input type="file" id="myfile" name="myfile" placeholder="Portrait Picture" className="form-control" />   
            </div>

            <div className="form-group mr-sm-2">
                <input
                id="productDesc"
                type="text"
                ref={(input) => { this.productDesc = input }}
                className="form-control"
                placeholder="Description"
                required />
            </div>

            <div className="form-group mr-sm-2">
                <input
                id="productPrice"
                type="text"
                ref={(input) => { this.productPrice = input }}
                className="form-control"
                placeholder="Price"
                required />
            </div>
          
          <div className="form-group mr-sm-2">
            <input
              id="productName"
              type="text"
              ref={(input) => { this.productName = input }}
              className="form-control"
              placeholder="Artist"
              required />
          </div>
          
          <center><button type="submit" className="btn btn-primary"> SAVE </button></center>
        </form>
    );
  }
}
export default Artists;
