import React, { Component } from 'react';
import Web3 from 'web3'
import './App.css';
import Marketplace from '../abis/Marketplace.json'
import Main from './Main'

class App extends Component {

  async componentWillMount() {
    await this.loadWeb3()
    await this.loadBlockchainData()
  }

  async loadWeb3() {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else {
      window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
    }
  }

  async loadBlockchainData() {
    const web3 = window.web3
    // Load account
    const accounts = await web3.eth.getAccounts()
    this.setState({ account: accounts[0] })
    const networkId = await web3.eth.net.getId()
    const networkData = Marketplace.networks[networkId]
    if(networkData) {
      const marketplace = web3.eth.Contract(Marketplace.abi, networkData.address)
      this.setState({ marketplace })
      const portraitCount = await marketplace.methods.portraitCount().call()
      this.setState({ portraitCount })
      // Load portraits
      for (var i = 1; i <= portraitCount; i++) {
        const portrait = await marketplace.methods.portraits(i).call()
        this.setState({
          portraits: [...this.state.portraits, portrait]
        })
      }
      this.setState({ loading: false})
    } else {
      window.alert('Marketplace contract not deployed to detected network.')
    }
  }

  constructor(props) {
    super(props)
    this.state = {
      account: '',
      portraitCount: 0,
      portraits: [],
      loading: true
    }

    this.createPortrait = this.createPortrait.bind(this)
    this.purchasePortrait = this.purchasePortrait.bind(this)
  }

  createPortrait(name, price) {
    this.setState({ loading: true })
    this.state.marketplace.methods.createPortrait(name, price).send({ from: this.state.account })
    .once('receipt', (receipt) => {
      this.setState({ loading: false })
    })
  }

  purchasePortrait(id, price) {
    this.setState({ loading: true })
    this.state.marketplace.methods.purchasePortrait(id).send({ from: this.state.account, value: price })
    .once('receipt', (receipt) => {
      this.setState({ loading: false })
    })
  }

  render() {
    return (
      <div>
        {/* <Navbar account={this.state.account} /> */}
        <div className="container-fluid mt-5">
          <div className="row">
            <main role="main" className="col-lg-12 d-flex">
              { this.state.loading
                ? <div id="loader" className="text-center"><p className="text-center">Loading...</p></div>
                : <Main
                  account={this.state.account}
                  portraits={this.state.portraits}
                  createPortrait={this.createPortrait}
                  purchasePortrait={this.purchasePortrait} />
              }
            </main>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
